// Load 2 word cloud left and right when finishing loading the page
function loadDefaultCloud() {
    console.log('loadDefaultCloud here')
    generateCloud(words["Business Analyst"], "left_cloud");
    generateCloud(words["Business Analyst"], "right_cloud");
}

var enableMouseover = true; // Flag variable to control mouseover cursor behavior

// Change the left word cloud when select differen job title
function onLeftSelectionChange(value) {
    console.log('onSelectionChange - ', value)
    generateCloud(words[value], "left_cloud");
    enableMouseover = true; // Enable mouseover cursor
    resetCursor();
    highlightOverlappingWords(); // Reset highlighting
}

function onRightSelectionChange(value) {
    console.log('onSelectionChange - ', value)
    generateCloud(words[value], "right_cloud");
    enableMouseover = true; // Enable mouseover cursor
    resetCursor();
    highlightOverlappingWords(); // Reset highlighting
}

// Reset cursor behavior for all text elements
function resetCursor() {
    document.body.style.cursor = "default";
}

// Create world cloud based on jobs and div container
function generateCloud(myWords, containerId) {
    var container = document.getElementById(containerId);
    container.innerHTML = '';

    // Set the dimensions and margins of the graph
    var margin = { top: 50, right: 10, bottom: 10, left: 10 };
    var windowWidth = window.innerWidth;
    var windowHeight = window.innerHeight;
    var width = windowWidth / 2 - 3 * margin.left - margin.right;
    var height = windowHeight / 1.3 - margin.top - margin.bottom;

    // Append the svg object to the container
    var svg = d3
        .select("#" + containerId)
        .append("svg")
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)
        .append("g")
        .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    // Constructs a new cloud layout instance
    var layout = d3.layout
        .cloud()
        .size([width, height])
        .words(
            myWords.map(function (d) {
                return { text: d.word, size: d.size };
            })
        )
        .padding(10)
        .rotate(function () {
            return Math.random() * 90 - 50;
        })
        .fontSize(function (d) {
            return d.size;
        })
        .on("end", draw);
    layout.start();

    function draw(words) {
        svg.append("g")
            .attr(
                "transform",
                "translate(" + layout.size()[0] / 2 + "," + layout.size()[1] / 2 + ")"
            )
            .selectAll("text")
            .data(words)
            .enter()
            .append("text")
            .style("font-size", function (d) {
                return d.size;
            })
            .style("fill", "#4C78A8")
            .attr("text-anchor", "middle")
            .style("font-family", "Impact")
            .attr("transform", function (d) {
                return "translate(" + [d.x, d.y] + ")rotate(" + d.rotate + ")";
            })
            .text(function (d) {
                return d.text;
            })
            .on("click", function (d) {
                highlightOverlappingWords(d.text);
                d3.selectAll("#left_cloud text, #right_cloud text")
                    .style("cursor", "default"); // Reset cursor for all words
                enableMouseover = false; // Disable mouseover cursor
            })
            .on("mouseover", function () {
                if (enableMouseover) {
                    d3.select(this).style("cursor", "pointer");
                } else {
                    d3.select(this).style("cursor", "default");
                }
            });
    }

    function highlightOverlappingWords(selectedWord) {
        var leftCloudWords = Array.from(document.querySelectorAll("#left_cloud text")).map(
            function (element) {
                return element.textContent;
            }
        );
        var rightCloudWords = Array.from(document.querySelectorAll("#right_cloud text")).map(
            function (element) {
                return element.textContent;
            }
        );

        var overlappingWords = leftCloudWords.filter(function (word) {
            return rightCloudWords.includes(word);
        });

        var allTextElements = document.querySelectorAll(
            "#left_cloud text, #right_cloud text"
        );
        allTextElements.forEach(function (element) {
            if (overlappingWords.includes(element.textContent)) {
                element.style.fill = "#F58518";
            } else {
                element.style.fill = "#4C78A8";
            }
        });
        
    }
}